DROP TABLE IF EXISTS `uha_orderlist`;
CREATE TABLE `uha_orderlist` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `userid` int(11) NOT NULL DEFAULT '0',
  `goods_id` int(11) NOT NULL DEFAULT '0' COMMENT '商品id',
  `dealerid` int(11) NOT NULL DEFAULT '0' COMMENT '经销商id   0是总部',
  `is_delivery` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否上门提货  0.否  1.是',
  `is_code` tinyint(1) NOT NULL DEFAULT '0' COMMENT '0未验证   1已验证',
  `code` varchar(6) DEFAULT NULL COMMENT '上门取货验证码',
  `storeid` int(11) NOT NULL COMMENT '门店id 用于is_delivery=1 的情况下',
  `orderno` varchar(20) NOT NULL COMMENT '订单号',
  `consignee` varchar(255) DEFAULT NULL COMMENT '收货人',
  `country` varchar(255) DEFAULT '' COMMENT '国家 ',
  `province` varchar(50) DEFAULT '' COMMENT '省份',
  `city` varchar(50) DEFAULT '' COMMENT '城市',
  `district` varchar(100) DEFAULT '' COMMENT '市/区/县',
  `address` varchar(255) DEFAULT NULL,
  `telephone` varchar(20) DEFAULT NULL COMMENT '手机号码',
  `orderstate` tinyint(1) NOT NULL DEFAULT '1' COMMENT '1未支付 2待发货 3待收货 4标记签收 5退款中 6取消订单 7.交易关闭',
  `pay_status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否支付 0.未支付  1.已支付',
  `shipping_status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '0 .未发货  1. 已发货',
  `confirm_status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '0.未收货 1.已收货',
  `addtime` datetime DEFAULT NULL COMMENT '订单提交时间',
  `paytime` datetime DEFAULT NULL COMMENT '支付时间',
  `shippingtime` datetime DEFAULT NULL COMMENT '发货时间',
  `confirmtime` datetime DEFAULT NULL COMMENT '确认收货时间',
  `refundtime` datetime DEFAULT NULL COMMENT '退款时间',
  `shipping_name` varchar(255) DEFAULT NULL,
  `confirm_name` varchar(255) DEFAULT NULL,
  `card_id` int(11) NOT NULL DEFAULT '0' COMMENT '优惠券id',
  `card_name` varchar(100) DEFAULT NULL COMMENT '优惠券名称',
  `card_message` varchar(255) DEFAULT NULL COMMENT '优惠券说明',
  `total_price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '总价',
  `shipping_fee` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '运费',
  `pay_fee` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '支付费用（微信支付）',
  `coupon_fee` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '优惠券抵消费用',
  `is_delete` int(11) NOT NULL DEFAULT '0',
  `paytype` int(11) NOT NULL DEFAULT '1' COMMENT '1余额支付  2微信支付',
  `is_refund` tinyint(1) NOT NULL DEFAULT '0' COMMENT '1申请退款 2退款成功 3不允许退款 4已收货取消申请退款',
  `refund_fee` decimal(10,2) DEFAULT NULL COMMENT '退款金额',
  `reason` varchar(255) DEFAULT NULL COMMENT '取消订单，退款原因',
  `comment` varchar(255) DEFAULT NULL COMMENT '订单备注',
  `is_groupbuy` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否是团购  0.否 1.是',
  `is_head` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否是团长 0.否 1.是',
  `groupbuyid` int(11) NOT NULL DEFAULT '0' COMMENT '团长订单id',
  `groupbuy_ok` tinyint(1) NOT NULL COMMENT '是否拼团成功  0.未成功  1.成功  2失败',
  `groupbuy_ok_time` datetime DEFAULT NULL COMMENT '团购成功的时间',
  `expressname` varchar(255) DEFAULT NULL COMMENT '物流名称',
  `expressno` varchar(100) DEFAULT NULL COMMENT '物流单号',
  `groupendtime` int(10) unsigned DEFAULT '0',
  `note` varchar(255) DEFAULT NULL COMMENT '取消订单：备注',
  `is_exist` tinyint(1) NOT NULL DEFAULT '1' COMMENT '后台标记是否有货  1为有货  2为无货',
  `barcode` varchar(255) DEFAULT NULL COMMENT '二维码地址',
  `online_order_fid` varchar(100) DEFAULT NULL COMMENT 'erp系统订单号',
  `xxw_order_status` tinyint(1) DEFAULT '0' COMMENT '新希望订单状态',
  `certain_success` tinyint(1) NOT NULL DEFAULT '0' COMMENT '团购是否一定成功 0否 1是',
  `ceshi` varchar(255) DEFAULT NULL,
  `wx_err_msg` varchar(255) DEFAULT NULL COMMENT '微信支付完成属性',
  `erpstoreid` int(11) DEFAULT '0' COMMENT 'erp门店编号',
  `arrive_time` tinyint(4) DEFAULT NULL COMMENT '0今日达1次日达',
  `channel` tinyint(1) DEFAULT '1',
  `reason_id` int(11) DEFAULT NULL,
  `return_content` varchar(500) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT '',
  `is_num` tinyint(1) DEFAULT NULL COMMENT '0.未处理1.减库存2.加回库存',
  PRIMARY KEY (`id`),
  KEY `groupbuyid` (`groupbuyid`),
  KEY `goods_id` (`goods_id`,`dealerid`),
  KEY `online_order_fid` (`online_order_fid`),
  KEY `orderno` (`orderno`),
  KEY `orderstate` (`orderstate`),
  KEY `refundtime` (`refundtime`),
  KEY `dealerid` (`dealerid`),
  KEY `addtime` (`addtime`),
  KEY `pay_status` (`pay_status`) USING HASH,
  KEY `total_price` (`total_price`),
  KEY `is_groupbuy` (`is_groupbuy`),
  KEY `userid` (`userid`),
  KEY `groupbuy_ok` (`groupbuy_ok`),
  KEY `telephone` (`telephone`) USING BTREE,
  KEY `pay_fee` (`pay_fee`),
  KEY `storeid` (`storeid`),
  KEY `is_delivery` (`orderstate`,`is_delivery`) USING BTREE,
  KEY `goods_id,storeid` (`goods_id`,`storeid`) USING BTREE,
  KEY `goods_id,orderstate` (`goods_id`,`orderstate`,`is_groupbuy`,`groupbuy_ok`) USING BTREE,
  KEY `is_refund` (`is_refund`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=535 DEFAULT CHARSET=utf8;

